// src/MyApp.js
import React, {useState, useEffect} from 'react';
import Table from './Table';
import Form from './Form';

function MyApp() {
	const [characters, setCharacters] = useState([]);


	function removeOneCharacter (index) {
	    const updated = characters.filter((character, i) => {
	        return i !== index
	    });
	  setCharacters(updated);
	}

	function updateList(person) { 
		postUser(person)
		  .then(() => setCharacters([...characters, person]))
		  .catch((error) => {
			console.log(error);
		  })
	}

	function postUser(person) {
		const promise = fetch("Http://localhost:8000/users", {
		  method: "POST",
		  headers: {
			"Content-Type": "application/json",
		  },
		  body: JSON.stringify(person),
		});

		return promise;
	  }


	function fetchUsers() {
    const promise = fetch("http://localhost:8000/users");
    return promise;
	}

	// Assuming you have a function to handle user insertion in your frontend
	function insertUser() {
	fetch('/insertUser', {
	  method: 'POST',
	  // Add request body and headers as needed
	})
	.then(response => {
	  if (response.status === 201) {
		// The user was successfully inserted (201 - Content Created)
		// You can update the frontend state or display a success message
		console.log('User created successfully');
	  } else {
		// Handle other status codes (e.g., 200, 400, 500, etc.) as needed
		// Don't update the state if the status code is not 201
		console.error('User creation failed');
	  }
	})
	.catch(error => {
	  // Handle network errors or other issues here
	  console.error('An error occurred:', error);
	});
  }


	useEffect(() => {
		fetchUsers()
			.then((res) => res.json())
			.then((json) => setCharacters(json["users_list"]))
			.catch((error) => { console.log(error); });
	  }, [] );

return (
  <div className="container">
    <Table characterData={characters} 
	    removeCharacter={removeOneCharacter} />
    <Form handleSubmit={updateList} />

  </div>
)

}

export default MyApp;
